import 'package:flutter/material.dart';import 'package:flutter_web_test/responsive.dart';import 'package:flutter_web_test/screens/dashboard/panels/redirect_panel.dart';import 'package:flutter_web_test/screens/dashboard/panels/text_dort_panel.dart';import 'package:flutter_web_test/screens/dashboard/panels/text_weee_panel.dart';import 'package:flutter_web_test/screens/dashboard/panels/top_text_panel.dart';class DashboardScreen extends StatelessWidget {  const DashboardScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Responsive(        desktop: _buildDesktop(context),        mobile: _buildMobile(context),      ),    );  }  Widget _buildDesktop(BuildContext context) {    return Row(      crossAxisAlignment: CrossAxisAlignment.stretch,      children: [        _buildLeftColumn(context),        _buildRightColumn(),      ],    );  }  Widget _buildMobile(BuildContext context) {    return const Expanded(      child: SingleChildScrollView(        child: Column(          children: [            TopTextPanel(),            RedirectPanel(),            TextDortPanel(),            TextWeeePanel(),          ],        ),      ),    );  }  Widget _buildLeftColumn(BuildContext context) {    return const Expanded(      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          Expanded(child: TopTextPanel()),          Expanded(flex: 2, child: RedirectPanel()),        ],      ),    );  }  Widget _buildRightColumn() {    return const Expanded(      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          Expanded(child: TextDortPanel()),          Expanded(child: TextWeeePanel()),        ],      ),    );  }}