import 'package:flutter/material.dart';import 'package:flutter_web_test/screens/dashboard/components/glass_morphism.dart';import 'package:go_router/go_router.dart';import '../../defaults.dart' as defaults;class DashboardScreen extends StatelessWidget {  const DashboardScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Row(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          _buildLeftColumn(context),          _buildRightColumn(),        ],      ),    );  }  Widget _buildLeftColumn(BuildContext context) {    return Expanded(      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          Expanded(child: GlassMorphism(child: const Text('Text hier'))),          _buildRedirectPanel(context),        ],      ),    );  }  Widget _buildRightColumn() {    return Expanded(      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [          Expanded(              child: GlassMorphism(child: const Align(alignment: Alignment.bottomRight, child: Text('Text dort')))),          Expanded(              child:                  GlassMorphism(child: const Align(alignment: Alignment.bottomRight, child: Text('Text weeeeeeee')))),        ],      ),    );  }  Widget _buildRedirectPanel(BuildContext context) {    return Expanded(      flex: 2,      child: GlassMorphism(        child: Center(          child: TextButton(            style: TextButton.styleFrom(padding: const EdgeInsets.all(defaults.mediumPadding)),            onPressed: () {              context.go('/todo');            },            child: const Text(              'Klick me ∠( ᐛ 」∠)_',              style: TextStyle(fontSize: defaults.defaultFontSize),            ),          ),        ),      ),    );  }}