import 'package:flutter/material.dart';import 'package:url_launcher/url_launcher.dart';import 'package:flutter_web_test/defaults.dart' as defaults;class Background extends StatelessWidget {  final Widget child;  const Background({    super.key,    required this.child,    this.topImage = "assets/images/icon-360-solutions.png",    this.bottomImage = "assets/images/mobile_logo_white.png",  });  final String topImage;  final String bottomImage;  @override  Widget build(BuildContext context) {    return Scaffold(      body: SizedBox(        width: double.infinity,        height: MediaQuery.of(context).size.height,        child: Stack(          alignment: Alignment.center,          children: <Widget>[            Positioned(              top: 0,              left: 0,              child: _buildRedirectingImage(                'https://www.eurofunk.com/products/command-control/resqnect-mobile-apps/',                topImage,              ),            ),            // Positioned(            //   bottom: 0,            //   right: 0,            //   child: Image.asset(bottomImage, width: 120),            // ),            SafeArea(child: child),          ],        ),      ),    );  }  Widget _buildRedirectingImage(String url, String imagePath) {    return MouseRegion(      cursor: SystemMouseCursors.click,      child: GestureDetector(        onTap: () {          launchUrl(Uri.parse(url));        },        child: Padding(          padding: const EdgeInsets.all(defaults.smallPadding),          child: Image.asset(            imagePath,            width: 70,          ),        ),      ),    );  }}