import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'components/background.dart';import '../../defaults.dart' as defaults;import '../../responsive.dart';import 'components/login_form.dart';class LoginScreen extends StatelessWidget {  const LoginScreen({super.key});  @override  Widget build(BuildContext context) {    return Background(      child: SingleChildScrollView(        child: Responsive(          desktop: _buildDesktopLoginScreen(context),          mobile: _buildMobileLoginScreen(context),        ),      ),    );  }  Widget _buildDesktopLoginScreen(BuildContext context) {    return Row(      mainAxisAlignment: MainAxisAlignment.spaceEvenly,      children: [        Image.asset(          'assets/images/mobile_logo_white.png',          height: MediaQuery.of(context).size.height * 0.35,        ),        const Row(          children: [            SizedBox(              width: 450,              child: LoginForm(),            )          ],        ),        const SizedBox(),      ],    );  }  Widget _buildMobileLoginScreen(BuildContext context) {    return SizedBox(      height: MediaQuery.of(context).size.height,      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          const SizedBox(height: defaults.standardPadding),          const Row(            children: [              Spacer(),              Expanded(                flex: 4,                child: LoginForm(),              ),              Spacer()            ],          ),          Image.asset(            'assets/images/mobile_logo_white.png',            height: MediaQuery.of(context).size.height * 0.25,            alignment: Alignment.bottomCenter,          ),        ],      ),    );  }}